import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [cabinets, setCabinets] = useState([]);
    const [selectedCabinets, setSelectedCabinets] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/cabinets').then(response => {
            setCabinets(response.data);
        }).catch(error => {
            console.error("There was an error fetching the cabinets!", error);
        });
    }, []);

    const handleSelection = (cabinetId) => {
        setSelectedCabinets(prev =>
            prev.includes(cabinetId) ? prev.filter(id => id !== cabinetId) : [...prev, cabinetId]
        );
    };

    const handleSubmit = () => {
        axios.post('http://localhost:5000/api/cabinets/selections', { cabinetIds: selectedCabinets });
    };

    return (
        <div>
            <h1>Kitchen Catalog</h1>
            <div>
                {cabinets.map(cabinet => (
                    <div key={cabinet._id}>
                        <h2>{cabinet.name}</h2>
                        <p>{cabinet.description}</p>
                        <button onClick={() => handleSelection(cabinet._id)}>
                            {selectedCabinets.includes(cabinet._id) ? 'Deselect' : 'Select'}
                        </button>
                    </div>
                ))}
            </div>
            <button onClick={handleSubmit}>Save Selection</button>
        </div>
    );
};

export default App;
