import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Checkbox, FormControlLabel } from '@mui/material';

const App = () => {
  const [cabinets, setCabinets] = useState([]);
  const [selectedCabinets, setSelectedCabinets] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/cabinets')
      .then(response => {
        setCabinets(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the cabinets!", error);
      });
  }, []);

  const handleSelection = (cabinetId) => {
    setSelectedCabinets(prev =>
      prev.includes(cabinetId) ? prev.filter(id => id !== cabinetId) : [...prev, cabinetId]
    );
  };

  const handleSubmit = () => {
    axios.post('http://localhost:5000/api/cabinets/selections', { cabinetIds: selectedCabinets });
  };

  return (
    <Container>
      <Typography variant="h3" gutterBottom>Kitchen Catalog</Typography>
      {['base', 'wall', 'tall'].map(type => (
        <div key={type}>
          <Typography variant="h4" gutterBottom>{`${type.charAt(0).toUpperCase() + type.slice(1)} Cabinets`}</Typography>
          <Grid container spacing={3}>
            {cabinets.filter(cabinet => cabinet.type === type).map(cabinet => (
              <Grid item xs={12} sm={6} md={4} key={cabinet._id}>
                <Card>
                  <div style={{ width: 256, height: 256, overflow: 'hidden', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <CardMedia
                      component="img"
                      style={{ maxWidth: '100%', maxHeight: '100%' }}
                      image={cabinet.image_url}
                      alt={cabinet.name}
                    />
                  </div>
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {cabinet.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {cabinet.description}
                    </Typography>
                    <FormControlLabel
                      control={
                        <Checkbox
                          checked={selectedCabinets.includes(cabinet._id)}
                          onChange={() => handleSelection(cabinet._id)}
                        />
                      }
                      label="Select"
                    />
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </div>
      ))}
      <button onClick={handleSubmit}>Save Selection</button>
    </Container>
  );
};

export default App;
