import React, { useState, useEffect } from 'react';
import './App.css';

const categoryColors = {
  base: '#6a0dad',  // Purple
  wall: '#1e90ff',  // DodgerBlue
  tall: '#00bfff'   // DeepSkyBlue
};

function App() {
  const [categories, setCategories] = useState({});
  const [selectedItems, setSelectedItems] = useState({});
  const [currentCategory, setCurrentCategory] = useState('base');

  useEffect(() => {
    // Fetch categories from the JSON file or API
    fetch('/categories.json')
      .then(response => response.json())
      .then(data => {
        setCategories(data);

        // Initialize selectedItems state based on the fetched categories
        const initialSelectedItems = {};
        Object.keys(data).forEach(category => {
          initialSelectedItems[category] = {};
          data[category].forEach(subcategory => {
            initialSelectedItems[category][subcategory] = [];
          });
        });
        setSelectedItems(initialSelectedItems);
      })
      .catch(error => console.error('Error fetching categories:', error));
  }, []);

  const handleSelectionChange = (category, subcategory, width) => {
    const updatedSelection = { ...selectedItems };
    const index = updatedSelection[category][subcategory].indexOf(width);
    if (index > -1) {
      updatedSelection[category][subcategory].splice(index, 1);
    } else {
      updatedSelection[category][subcategory].push(width);
    }
    setSelectedItems(updatedSelection);
  };

  const handleReset = () => {
    const resetSelectedItems = {};
    Object.keys(categories).forEach(category => {
      resetSelectedItems[category] = {};
      categories[category].forEach(subcategory => {
        resetSelectedItems[category][subcategory] = [];
      });
    });
    setSelectedItems(resetSelectedItems);
  };

  const getSelectedCount = (category) => {
    return Object.values(selectedItems[category] || {}).flat().length;
  };

  return (
    <div className="App">
      <h1>Kitchen Catalog</h1>
      <div className="tabs">
        {Object.keys(categories).map(category => (
          <div
            key={category}
            className={`tab ${currentCategory === category ? 'active' : ''}`}
            onClick={() => setCurrentCategory(category)}
          >
            {category.toUpperCase()}
            {getSelectedCount(category) > 0 && (
              <span className="counter" style={{ backgroundColor: categoryColors[category] }}>
                {getSelectedCount(category)}
              </span>
            )}
          </div>
        ))}
      </div>
      <div className="content">
        {categories[currentCategory] && categories[currentCategory].map(subcategory => (
          <div key={subcategory} className="card">
            <h2>{subcategory}</h2>
            <div className="checkbox-group">
              {['30cm', '40cm', '60cm'].map(width => (
                <div key={width}>
                  <input
                    type="checkbox"
                    checked={selectedItems[currentCategory] && selectedItems[currentCategory][subcategory]?.includes(width)}
                    onChange={() => handleSelectionChange(currentCategory, subcategory, width)}
                  />
                  <label>{width}</label>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      <button onClick={handleReset} className="reset-button">RESET</button>
    </div>
  );
}

export default App;
