import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [cabinets, setCabinets] = useState([]);
    const [selectedCabinets, setSelectedCabinets] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [token, setToken] = useState('');

    useEffect(() => {
        axios.get('http://localhost:5000/api/cabinets').then(response => {
            setCabinets(response.data);
        });
    }, []);

    const handleSelection = (cabinetId) => {
        setSelectedCabinets(prev =>
            prev.includes(cabinetId) ? prev.filter(id => id !== cabinetId) : [...prev, cabinetId]
        );
    };

    const handleRegister = async () => {
        const response = await axios.post('http://localhost:5000/api/users/register', { username, email, password });
        if (response.status === 201) {
            alert('User registered successfully');
        }
    };

    const handleLogin = async () => {
        const response = await axios.post('http://localhost:5000/api/users/login', { email, password });
        if (response.data.token) {
            setToken(response.data.token);
        }
    };

    const handleSubmit = () => {
        axios.post('http://localhost:5000/api/cabinets/selections', { userId: 1, cabinetIds: selectedCabinets }, {
            headers: { Authorization: `Bearer ${token}` }
        });
    };

    return (
        <div>
            <h1>Kitchen Catalog</h1>
            <div>
                <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                <button onClick={handleRegister}>Register</button>
                <button onClick={handleLogin}>Login</button>
            </div>
            <div>
                {cabinets.map(cabinet => (
                    <div key={cabinet._id}>
                        <h2>{cabinet.name}</h2>
                        <p>{cabinet.description}</p>
                        <button onClick={() => handleSelection(cabinet._id)}>
                            {selectedCabinets.includes(cabinet._id) ? 'Deselect' : 'Select'}
                        </button>
                    </div>
                ))}
            </div>
            <button onClick={handleSubmit}>Save Selection</button>
        </div>
    );
};

export default App;
