import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [cabinets, setCabinets] = useState([]);
  const [selectedCabinets, setSelectedCabinets] = useState({});
  const [selectedDimensions, setSelectedDimensions] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/cabinets')
      .then(response => {
        setCabinets(response.data);
      })
      .catch(error => {
        console.error('Error fetching cabinets:', error);
      });
  }, []);

  const handleSelect = (type, subcatName, width) => {
    setSelectedCabinets(prev => ({ ...prev, [type]: subcatName }));
    setSelectedDimensions(prev => ({
      ...prev,
      [`${type}-${subcatName}`]: width
    }));
  };

  const handleSubmit = () => {
    axios.post('http://localhost:5000/api/save-selections', {
      selectedCabinets,
      selectedDimensions
    }).then(response => {
      console.log('Selections saved:', response.data);
    }).catch(error => {
      console.error('Error saving selections:', error);
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Kitchen Cabinet Selector</h1>
      </header>
      <div className="container">
        {cabinets.map(cabinet => (
          <div key={cabinet._id}>
            <h2>{cabinet.name}</h2>
            {cabinet.subcategories.map(subcat => (
              <div key={subcat.name}>
                <h3>{subcat.name}</h3>
                {subcat.dimensions.map(dim => (
                  <button
                    key={dim.width}
                    onClick={() => handleSelect(cabinet.type, subcat.name, dim.width)}
                  >
                    {dim.width}
                  </button>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Save Selections</button>
    </div>
  );
}

export default App;
